var concat=require("../../"),spawn=require("child_process").spawn,exec=require("child_process").exec,test=require("tape");test("ls command",function(e){e.plan(1);var t=spawn("ls",[__dirname]);t.stdout.pipe(concat(function(t){exec("ls "+__dirname,function(n,r){e.equal(t.toString("utf8"),r.toString("utf8"))})}))});